// stolen from SO.
function syntaxHighlight(json, nohtml) {
    if (typeof json !== 'string') {
         json = JSON.stringify(json, undefined, 2);
    }
    json = json.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;');
    return json.replace(/("(\\u[a-zA-Z0-9]{4}|\\[^u]|[^\\"])*"(\s*:)?|\b(true|false|null)\b|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?)/g, function (match) {
        var cls = 'number';
        if (/^"/.test(match)) {
            if (/:$/.test(match)) {
                cls = 'key';
            } else {
                cls = 'string';
            }
        } else if (/true|false/.test(match)) {
            cls = 'boolean';
        } else if (/null/.test(match)) {
            cls = 'null';
        }
        return '<span class="' + cls + '">' + match + '</span>';
    });
}

var Log = Backbone.View.extend({
    el: "#log",
    initialize: function() {
        var self = this;
        self.count = 0;

        ['log','info','error'].forEach(function(type) {
            var orin = console[type];
            console[type] = function(str) {
                orin.call(console, arguments[0]);
                self.log(str, type);
                //orin.apply(console, Array.prototype.splice.call(arguments));
            };
        });
    },
    log: function(str, type) {
        type = type || "log";

        function datafy(obj) {
            var tr;
            if ($.isArray(obj)) {
                tr = [];
                obj.forEach(function(x) {
                    tr.push(datafy(x));
                });
                return tr;
            } else if (typeof obj === 'object') {
                tr = [];
                for (var key in obj) {
                    if (obj.hasOwnProperty(key)) {
                        tr.push( {
                            label: key,
                            children: datafy(obj[key])
                        });
                    }
                }
                return tr;
            } else {
                return [{label: obj}];
            }
        }
        if (typeof str === 'object') {
            this.$el.prepend(
                $('<div class="log-'+type+'"/>').tree( {
                    data: datafy(str),
                    autoOpen: true,
                    slide: false
                }));
        } else {

            this.$el.prepend(tmpl.log({
                count: ++this.count,
                log: str,
                type: type
            }));
        }
    }
});

